var pontosJogador = 0
var pontosMaquina = 0

atualizaPlacar()
atualizaQuantidadeDeCartas()

function atualizaQuantidadeDeCartas(){
  var divQuantidadeCartas = document.getElementById('quantidade-cartas')
  var html = "Quantidade de cartas no jogo: " + cartas.length
  
  divQuantidadeCartas.innerHTML = html
}

function atualizaPlacar(){
  var divPlacar = document.getElementById('placar')
  var html = "Jogador " + pontosJogador + "/" + pontosMaquina + " Máquina"
  
  divPlacar.innerHTML = html
}

function sortearCarta(){
  var numeroCartaMaquina = parseInt(Math.random() * cartas.length)
  cartaMaquina = cartas[numeroCartaMaquina]
   cartas.splice(numeroCartaMaquina, 1)
  
  var numeroCartaJogador = parseInt(Math.random() * cartas.length)
     cartaJogador = cartas[numeroCartaJogador]
     cartas.splice(numeroCartaJogador, 1)
  
  console.log(cartaJogador)
//Leon Denis Silvestre de Lucena
  document.getElementById('btnSortear').disabled = true
  document.getElementById('btnJogar').disabled = false
  
  
  
  imagemCartaJogador = document.getElementById("cartaJogador")
  imagemCartaJogador.src = cartaJogador.imagem
  var imagemVersos = document.getElementById("versos")
  imagemVersos.src = "http://c.dx.com/collection/201804/20180410/images/vs.png"
  imagemCartaMaquina = document.getElementById("cartaMaquina")
  imagemCartaMaquina.src = "https://i.imgur.com/NxLc6x5.png"

  exibirOpcoes()
  
function exibirOpcoes() {
  var opcoes = document.getElementById('opcoes')
  var opcoesTexto =""
  for (var atributo in cartaJogador.atributos){
    opcoesTexto += "<input type='radio' name='atributo' value='" + atributo + "' checked>" + atributo 
  }
  opcoes.innerHTML = opcoesTexto
}
}

function jogar(){
  imagemCartaMaquina.src = cartaMaquina.imagem
  var status = document.getElementById("status")
  var atributoSelecionado = obtemAtributoSelecionado()
  if(cartaJogador.atributos[atributoSelecionado] > cartaMaquina.atributos[atributoSelecionado]){
    status.innerText = "Você Venceu!"
    pontosJogador++
    document.getElementById('btnReiniciar').disabled = false 
      document.getElementById('btnJogar').disabled = true    
  }
  else if(cartaJogador.atributos[atributoSelecionado] < cartaMaquina.atributos[atributoSelecionado]){
    status.innerText = "Você Perdeu!"
    pontosMaquina++
      document.getElementById('btnReiniciar').disabled = false
    document.getElementById('btnJogar').disabled = true
    
  }
  else{status.innerText = "Empate!"}
    document.getElementById('btnReiniciar').disabled = false
  document.getElementById('btnJogar').disabled = true
  
    if (cartas.length == 0){
    alert("Fim de Jogo")
    if(pontosJogador > pontosMaquina){
      status.innerText = 'Venceu o jogo'
        document.getElementById('btnReiniciar').disabled = true 

    } else if (pontosMaquina > pontosJogador){
      status.innerText ='Perdeu o jogo'
        document.getElementById('btnReiniciar').disabled = true 

    } else {
      status.innerText = 'O jogo Empatou'
        document.getElementById('btnReiniciar').disabled = true 

    }
  } 

  document.getElementById('btnJogar').disabled = true
  atualizaPlacar()
  atualizaQuantidadeDeCartas()
}

function obtemAtributoSelecionado() {
  var radioAtributo = document.getElementsByName('atributo')
  for (var i = 0; i < radioAtributo.length; i++){
    if (radioAtributo[i].checked){
      return radioAtributo[i].value
    }
  }
}

function jogarNovamente(){
  var divStatus = document.getElementById("status")
  var divCartaJogador = document.getElementById("carta-jogador")
  var divCartaMaquina = document.getElementById("carta-maquina")
  var atributoSelecionado = obtemAtributoSelecionado()
  document.getElementById('btnSortear').disabled = false
  document.getElementById('btnJogar').disabled = true
  document.getElementById('btnReiniciar').disabled = true 
}

